declare module "asteria-gaia" {

export abstract class AbstractAsteriaObject implements AsteriaObject {    private readonly _className;    constructor(className: string);    getClassName(): string;}export interface AsteriaLine {    index: number;    value: string;}export interface AsteriaObject {    getClassName(): string;}export class AsteriaError extends AbstractAsteriaObject {    constructor(code: AsteriaErrorCode, className: string, message: string, stack?: string);    readonly code: AsteriaErrorCode;    readonly message: string;    readonly className: string;    readonly stack: string;    toString(): string;}export enum AsteriaErrorCode {    PROCESS_FAILURE = 100,    MISSING_PARAMETER = 200,    INVALID_PARAMETER = 201,    MISSING_FILTER = 202,    INVALID_FILTER = 203,    MISSING_ASQL_QUERY = 300,    INVALID_ASQL_QUERY = 301,    INVALID_ASQL_OPERAND = 302,    INVALID_ASQL_CONDITION = 303}export enum CommonChar {    EMPTY = "",    WHITE_SPACE = " ",    COMMA = ",",    NEW_LINE = "\n"}export class CommonRegExp {    static readonly NEW_LINE: RegExp;}export enum PrimitiveType {    STRING = "string",    NUMBER = "number",    ARRAY = "array",    OBJECT = "object"}export interface AsteriaSessionConfig {    name: string;}export interface StreamProcessConfig {    [key: string]: any;}export interface AsteriaContext {    getName(): string;    getId(): string;    getProcessor(): StreamProcessor;}export interface AsteriaSession {    getContext(): AsteriaContext;}export interface AsteriaStream extends AsteriaObject {    init(config: StreamProcessConfig, context: AsteriaContext): void;}export interface AsteriaFilter {    operators: Array<FilterOperator | FilterOperatorNotation | string>;    apply(obj: any, property: string, value: any): boolean;}export enum FilterCondition {    AND = "AND",    OR = "OR"}export interface FilterDefinition {    property: string;    operator: FilterOperator;    value: any;}export enum FilterOperator {    EQUAL = "EQUAL",    NOT_EQUAL = "NOT_EQUAL",    LIKE = "LIKE",    NOT_LIKE = "NOT_LIKE",    STARTS_WITH = "STARTS_WITH",    ENDS_WITH = "ENDS_WITH",    GREATER_THAN = "GREATER_THAN",    LOWER_THAN = "LOWER_THAN"}export enum FilterOperatorNotation {    EQUAL = "=",    NOT_EQUAL = "!=",    GREATER_THAN = ">",    LOWER_THAN = "<"}export interface StreamProcess extends AsteriaObject {    getConfig(): StreamProcessConfig;    setConfig(config: StreamProcessConfig): void;    getType(): StreamProcessType;    create(context: AsteriaContext): AsteriaStream;}export interface StreamProcessor {    add(process: StreamProcess): StreamProcessor;    remove(process: StreamProcess): StreamProcessor;    run(): AsteriaStream;}export enum StreamProcessType {    READABLE = 0,    TRANSFORM = 1,    WRITABLE = 2}export interface AsteriaLogger {    getLogLevel(): AsteriaLogLevel;    setLogLevel(level: AsteriaLogLevel): void;    log(level: AsteriaLogLevel, message: string): void;    debug(message: string): void;    info(message: string): void;    warn(message: string): void;    error(message: string): void;    fatal(message: string): void;}export enum AsteriaLogLevel {    DEBUG = 0,    INFO = 1,    WARN = 2,    ERROR = 3,    FATAL = 4}export interface StreamProcessBuilder {    build(processClass: any, config?: StreamProcessConfig): StreamProcess;}}